import React, { useState } from 'react';

function TaskForm({ textTask, handleChange, handleSubmit }) {
  const [taskStatus, setTaskStatus] = useState('A faire'); 

  const handleStatusChange = (e) => {
    setTaskStatus(e.target.value); 
  };

  return (
    <form onSubmit={(e) => handleSubmit(e, taskStatus)}>
      <label>
        Entrez votre t√¢che: <input name="tache" value={textTask} onChange={handleChange} />
      </label>
      <label>
        Statut:
        <select value={taskStatus} onChange={handleStatusChange}>
          <option value='A faire'>A faire</option>
          <option value='En cours'>En cours</option>
          <option value='Fait'>Fait</option>
        </select>
      </label>
      <button type='submit'>Enregistrer</button>
    </form>
  );
}

export default TaskForm;
