import React, { useState, useEffect } from 'react';

function App() {
  const [Tasks, setTasks] = useState([]);
  const [TextTasks, setTextTask] = useState('');
  const [hasLoaded, setHasLoaded] = useState(false); 

  // const task = {
  //   text: '',
  //   status: 'todo',
  // }

  const handleChange = (e) => {
    setTextTask(e.target.value);
  };

  const handleStatusChange = (index, newStatus) => {
    setTasks((prevTasks) => {
      return prevTasks.map((task, i) =>{
        if (i === index){
          return { ...task, status: newStatus};
        }
        return task;
      })
    })
  }

  // Charger les tâches depuis localStorage au démarrage
  useEffect(() => {
    const storedTasks = localStorage.getItem('Tasks');

    if (storedTasks) {
      const parsedTasks = JSON.parse(storedTasks);
      setTasks(parsedTasks);
    }
    setHasLoaded(true);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (TextTasks.trim() !== '') {
      const newTask = {
        text: TextTasks,
        status: 'todo',
      };

      // Ajouter la tâche dans le tableau
      setTasks((prevTasks) => [...prevTasks, newTask]);

      // On vide le champ de saisie
      setTextTask('');
    }
  };

  const handleDelete = (index) => {
    // Log Supprimer la tâche avec l'id
    console.log('Tâche numéro : ',index,' supprimée');
    setTasks((prevTasks) => prevTasks.filter((_, i) => i !== index));
  }

  // Sauvegarder dans localStorage lorsque Tasks change, après le chargement initial
  useEffect(() => {
    if (hasLoaded) {
      localStorage.setItem('Tasks', JSON.stringify(Tasks));
    }
  }, [Tasks, hasLoaded]); 

  return (
    <div>
      <h1>Liste de Tâches</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Entrez votre tâche: <input name="tache" value={TextTasks} onChange={handleChange} />
        </label>
        <button type='submit'>
          Enregistrer
        </button>
      </form>
      {Tasks.map((task, index) => (
        <p key={index}>
          {task.text} - {task.status}
          <select value={task.status} onChange={(e) => handleStatusChange(index, e.target.value)}>
            <option value='À faire'>A faire</option>
            <option value='En cours'>En cours</option>
            <option value='Terminé'>Fait</option>
          </select>
          <button type='delete' onClick={() =>handleDelete(index)}>
            Supprimer
          </button>
        </p>
      ))}
    </div>
  );
}

export default App;
