import React from 'react';
import TaskItem from './TaskItem';
import { Droppable } from 'react-beautiful-dnd';

function TaskList({ Tasks, handleDelete }) {
  console.log("Rendering TaskList with Tasks:", Tasks);

  return (
    <div style={{ display: 'flex', justifyContent: 'space-around' }}>
      {/* Colonne "A faire" */}
      <Droppable droppableId="todo">
        {(provided) => {
          console.log("Rendering Droppable with id 'todo'");
          return (
            <div ref={provided.innerRef} {...provided.droppableProps} style={{ minHeight: '200px', width: '30%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px' }}>
              <h2>A faire</h2>
              {Tasks.todo && Tasks.todo.length > 0 ? (
                Tasks.todo.map((task, index) => (
                  <TaskItem
                    key={task.id}
                    task={task}
                    index={index}
                    handleDelete={handleDelete}
                  />
                ))
              ) : (
                <p>Aucune tâche</p>
              )}
              {provided.placeholder}
            </div>
          );
        }}
      </Droppable>

      {/* Colonne "En cours" */}
      <Droppable droppableId="in-progress">
        {(provided) => {
          console.log("Rendering Droppable with id 'in-progress'");
          return (
            <div ref={provided.innerRef} {...provided.droppableProps} style={{ minHeight: '200px', width: '30%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px' }}>
              <h2>En cours</h2>
              {Tasks['in-progress'] && Tasks['in-progress'].length > 0 ? (
                Tasks['in-progress'].map((task, index) => (
                  <TaskItem
                    key={task.id}
                    task={task}
                    index={index}
                    handleDelete={handleDelete}
                  />
                ))
              ) : (
                <p>Aucune tâche</p>
              )}
              {provided.placeholder}
            </div>
          );
        }}
      </Droppable>

      {/* Colonne "Fait" */}
      <Droppable droppableId="done">
        {(provided) => {
          console.log("Rendering Droppable with id 'done'");
          return (
            <div ref={provided.innerRef} {...provided.droppableProps} style={{ minHeight: '200px', width: '30%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px' }}>
              <h2>Fait</h2>
              {Tasks.done && Tasks.done.length > 0 ? (
                Tasks.done.map((task, index) => (
                  <TaskItem
                    key={task.id}
                    task={task}
                    index={index}
                    handleDelete={handleDelete}
                  />
                ))
              ) : (
                <p>Aucune tâche</p>
              )}
              {provided.placeholder}
            </div>
          );
        }}
      </Droppable>
    </div>
  );
}

export default TaskList;
