import React from 'react';
import { Draggable } from 'react-beautiful-dnd';
import { FaGripLines } from 'react-icons/fa';  // Exemple d'icône de FontAwesome (ou toute autre bibliothèque d'icônes)

function TaskItem({ task, index, handleStatusChange, handleDelete }) {
  return (
    <Draggable draggableId={task.id} index={index}>
      {(provided) => (
        <div 
          ref={provided.innerRef} 
          {...provided.draggableProps}  // Ne l'applique pas à tout l'élément
        >
          {/* Ajoute l'icône de drag handle ici */}
          <span {...provided.dragHandleProps}>
            <FaGripLines style={{ cursor: 'grab', marginRight: '10px' }} /> {/* Icône de glisser-déposer */}
          </span>

          {/* Le reste de l'élément */}
          {task.text} - {task.status}
          <button type='delete' onClick={() => handleDelete(index)}>Supprimer</button>
          <select value={task.status} onChange={(e) => handleStatusChange(index, e.target.value)}>
            <option value='todo'>A faire</option>
            <option value='in-progress'>En cours</option>
            <option value='done'>Fait</option>
          </select>
        </div>
      )}
    </Draggable>
  );
}

export default TaskItem;
