function TaskList({ Tasks, handleStatusChange, handleDelete }) {
  const tasksTodo = Tasks.filter(task => task.status === 'A faire');
  const tasksEnCours = Tasks.filter(task => task.status === 'En cours');
  const tasksFait = Tasks.filter(task => task.status === 'Fait');

  return (
    <div style={{ display: 'flex', justifyContent: 'space-around' }}>
      {/* Colonne "A faire" */}
      <div>
        <h2>A faire</h2>
        {tasksTodo.map((task, index) => (
          <TaskItem 
            key={index}
            task={task} 
            index={index} 
            handleStatusChange={handleStatusChange} 
            handleDelete={handleDelete}
          />
        ))}
      </div>

      {/* Colonne "En cours" */}
      <div>
        <h2>En cours</h2>
        {tasksEnCours.map((task, index) => (
          <TaskItem 
            key={index}
            task={task} 
            index={index} 
            handleStatusChange={handleStatusChange} 
            handleDelete={handleDelete}
          />
        ))}
      </div>

      {/* Colonne "Fait" */}
      <div>
        <h2>Fait</h2>
        {tasksFait.map((task, index) => (
          <TaskItem 
            key={index}
            task={task} 
            index={index} 
            handleStatusChange={handleStatusChange} 
            handleDelete={handleDelete}
          />
        ))}
      </div>
    </div>
  );
}

export default TaskList;
