import React, { useState, useEffect } from 'react';

function App() {
  const [Tasks, setTasks] = useState([]);
  const [TextTasks, setTextTask] = useState('');
  const [hasLoaded, setHasLoaded] = useState(false); 

  const handleChange = (e) => {
    setTextTask(e.target.value);
  };

  // Charger les tâches depuis localStorage au démarrage
  useEffect(() => {
    const storedTasks = localStorage.getItem('Tasks');
    console.log("Tâches chargées depuis localStorage:", storedTasks);

    if (storedTasks) {
      const parsedTasks = JSON.parse(storedTasks);
      setTasks(parsedTasks);
      console.log("Tasks après chargement de localStorage:", parsedTasks);
    }
    setHasLoaded(true);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (TextTasks.trim() !== '') {
      setTasks((prevTasks) => [...prevTasks, TextTasks]);
      setTextTask('');
    }
  };

  // Sauvegarder dans localStorage lorsque Tasks change, après le chargement initial
  useEffect(() => {
    if (hasLoaded) {
      console.log("Sauvegarde des tâches dans localStorage:", Tasks);
      localStorage.setItem('Tasks', JSON.stringify(Tasks));
    }
  }, [Tasks, hasLoaded]); // Dépend de Tasks et du drapeau hasLoaded

  return (
    <div>
      <h1>Liste de Tâches</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Entrez votre tâche: <input name="tache" value={TextTasks} onChange={handleChange} />
        </label>
        <button type='submit'>
          Enregistrer
        </button>
      </form>
      {Tasks.map((task, index) => (
        <p key={index}>{task}</p>
      ))}
    </div>
  );
}

export default App;
