import React, { useState, useEffect } from 'react';
import TaskForm from './components/TaskForm';  
import TaskList from './components/TaskList'; 
import { DragDropContext } from 'react-beautiful-dnd';

function App() {
  const [Tasks, setTasks] = useState([]);
  const [TextTasks, setTextTask] = useState('');
  const [hasLoaded, setHasLoaded] = useState(false); 

  const handleChange = (e) => {
    setTextTask(e.target.value);
  };

  const handleStatusChange = (index, newStatus) => {
    setTasks((prevTasks) => prevTasks.map((task, i) => i === index ? { ...task, status: newStatus } : task));
  };

  const handleSubmit = (e, status) => {
    e.preventDefault();
    if (TextTasks.trim() !== '') {
      const newTask = { id: Date.now().toString(), text: TextTasks, status };
      console.log('New Task ID:', newTask.id);  // Vérification de l'ID
      setTasks((prevTasks) => [...prevTasks, newTask]);
      setTextTask('');
    }
  };



  const handleDelete = (index) => {
    setTasks((prevTasks) => prevTasks.filter((_, i) => i !== index));
  };

  const handleOnDragEnd = (result) => {
    const { source, destination } = result;
    console.log("Source:", source, "Destination:", destination);

    if (!destination) return;

    const updatedTasks = Array.from(Tasks);
    const [movedTask] = updatedTasks.splice(source.index, 1);
    console.log("Moved Task:", movedTask);

    movedTask.status = destination.droppableId;
    updatedTasks.splice(destination.index, 0, movedTask);

    setTasks(updatedTasks);
  };


  useEffect(() => {
    const storedTasks = localStorage.getItem('Tasks');
    if (storedTasks) {
      setTasks(JSON.parse(storedTasks));
    }
    setHasLoaded(true);
  }, []);

  useEffect(() => {
    if (hasLoaded) {
      localStorage.setItem('Tasks', JSON.stringify(Tasks));
    }
  }, [Tasks, hasLoaded]);

  return (
    <div>
      <h1>Liste de Tâches</h1>
      <TaskForm TextTasks={TextTasks} handleChange={handleChange} handleSubmit={handleSubmit} />
      {Tasks.length > 0 && (  // N'affiche le DragDropContext que lorsque les tâches sont chargées
        <DragDropContext onDragEnd={handleOnDragEnd}>
          <TaskList Tasks={Tasks} handleStatusChange={handleStatusChange} handleDelete={handleDelete} />
        </DragDropContext>
      )}
    </div>
  );
}

export default App;
