import React, {useState} from 'react';
import {useEffect} from 'react';

function App(){
  const [Tasks, setTasks] = useState([]);
  const [TextTasks, setTextTask] = useState('');

  const handleChange = (e) => {
    setTextTask(e.target.value);
  }

  useEffect (() => {
    const storedTasks = localStorage.getItem('Tasks');
    console.log("Tâches chargées depuis localStorage:", storedTasks);

    if (storedTasks){
      setTasks(JSON.parse(storedTasks));
    } 
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (TextTasks.trim() !== ''){
      setTasks([...Tasks, TextTasks]);
      setTextTask('');
    }
  } 

  useEffect(() => {
    console.log("Sauvegarde des tâches dans localStorage:", Tasks);
    localStorage.setItem('Tasks', JSON.stringify(Tasks));
  }, [Tasks]);

  return (
    <div>
      <h1>Liste de Tâches</h1>
      <form onSubmit={handleSubmit}>
        <label>
            Entrez vôtre tâche: <input name="tache" value={TextTasks} onChange={handleChange} />
        </label>
        <button type='submit'>
          Enregistrer
        </button>
      </form>
      {Tasks.map((task, index) => (
        <p key={index}>{task}</p>
      )
      )};
    </div>
  )
}

export default App;
