import React from 'react';
import { Draggable } from 'react-beautiful-dnd';
import { FaGripLines } from 'react-icons/fa';

function TaskItem({ task, index, handleDelete }) {
  return (
    <Draggable draggableId={task.id} index={index}>
      {(provided, snapshot) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          style={{
            ...provided.draggableProps.style,
            padding: '10px',
            margin: '0 0 8px 0',
            backgroundColor: snapshot.isDragging ? '#e0e0e0' : '#fff',
            border: '1px solid #ccc',
            borderRadius: '4px',
            display: 'flex',
            alignItems: 'center',
          }}
        >
          <span {...provided.dragHandleProps} style={{ marginRight: '10px', cursor: 'grab' }}>
            <FaGripLines />
          </span>
          <div style={{ flexGrow: 1 }}>
            {task.text}
          </div>
          <button type='button' onClick={() => handleDelete(task.id)}>Supprimer</button>
        </div>
      )}
    </Draggable>
  );
}

export default TaskItem;
