import React from 'react';
import { Draggable } from 'react-beautiful-dnd';
import { FaGripLines } from 'react-icons/fa';

function TaskItem({ task, index, handleDelete }) {
  console.log("Rendering TaskItem:", task.id);

  return (
    <Draggable draggableId={task.id.toString()} index={index}>
      {(provided, snapshot) => {
        console.log('Draggable ID:', task.id);
        return (
          <div
            ref={provided.innerRef}
            {...provided.draggableProps}
            style={{
              ...provided.draggableProps.style,
              padding: '10px',
              margin: '0 0 8px 0',
              backgroundColor: snapshot.isDragging ? '#e0e0e0' : '#fff',
              border: '1px solid #ccc',
              borderRadius: '4px',
              display: 'flex',
              alignItems: 'center'
            }}
          >
            {/* Icône de drag handle */}
            <span {...provided.dragHandleProps} style={{ marginRight: '10px', cursor: 'grab' }}>
              <FaGripLines />
            </span>

            {/* Le reste de l'élément */}
            <div style={{ flexGrow: 1 }}>
              {task.text}
            </div>
            <button type='button' onClick={() => handleDelete(task.id, task.status)}>Supprimer</button>
          </div>
        );
      }}
    </Draggable>
  );
}

export default TaskItem;
