import React, { useState, useEffect } from 'react';
import { FaEdit, FaTrash, FaSave, FaPlus } from 'react-icons/fa';

function App() {
  const [tasks, setTasks] = useState(() => {
    const savedTasks = localStorage.getItem('tasks');
    return savedTasks ? JSON.parse(savedTasks) : [];
  });
  const [textTask, setTextTask] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const handleChange = (e) => {
    setTextTask(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (textTask.trim() !== '') {
      if (editingIndex !== null) {
        const updatedTasks = [...tasks];
        updatedTasks[editingIndex] = textTask;
        setTasks(updatedTasks);
        setEditingIndex(null);
      } else {
        setTasks([...tasks, textTask]);
      }
      setTextTask('');
    }
  };

  const handleEdit = (index) => {
    setTextTask(tasks[index]);
    setEditingIndex(index);
  };

  const handleDelete = (index) => {
    const updatedTasks = tasks.filter((_, i) => i !== index);
    setTasks(updatedTasks);
  };

  return (
    <div style={{ maxWidth: '500px', margin: '20px auto', fontFamily: 'Arial, sans-serif', textAlign: 'center' }}>
      <h1 style={{ color: '#4CAF50' }}>Liste de Tâches</h1>
      <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
        <label style={{ display: 'block', marginBottom: '10px' }}>
          Entrez votre tâche:
          <input
            name="tache"
            value={textTask}
            onChange={handleChange}
            style={{ padding: '10px', width: '80%', margin: '10px 0' }}
          />
        </label>
        <button type='submit' style={{ padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '5px' }}>
          {editingIndex !== null ? <FaSave /> : <FaPlus />} {editingIndex !== null ? 'Modifier' : 'Enregistrer'}
        </button>
      </form>
      {tasks.map((task, index) => (
        <div key={index} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', backgroundColor: '#f9f9f9', padding: '10px', margin: '10px 0', borderRadius: '5px' }}>
          <p style={{ margin: '0', flexGrow: 1, textAlign: 'left' }}>{task}</p>
          <button onClick={() => handleEdit(index)} style={{ marginRight: '10px', padding: '5px 10px', backgroundColor: '#ff9800', color: 'white', border: 'none', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '5px' }}>
            <FaEdit /> Modifier
          </button>
          <button onClick={() => handleDelete(index)} style={{ padding: '5px 10px', backgroundColor: '#f44336', color: 'white', border: 'none', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '5px' }}>
            <FaTrash /> Supprimer
          </button>
        </div>
      ))}
    </div>
  );
}

export default App;
