import React from 'react';
import TaskColumn from './TaskColumn/TaskColumn';

function TaskList({ tasks, handleStatusChange, handleDelete }) {
  const tasksTodo = tasks.filter(task => task.status === 'A faire');
  const tasksEnCours = tasks.filter(task => task.status === 'En cours');
  const tasksFait = tasks.filter(task => task.status === 'Fait');

  return (
    <div style={{ display: 'flex', justifyContent: 'space-around' }}>
      <TaskColumn 
        title="A faire" 
        tasks={tasksTodo} 
        handleStatusChange={handleStatusChange} 
        handleDelete={handleDelete} 
      />
      <TaskColumn 
        title="En cours" 
        tasks={tasksEnCours} 
        handleStatusChange={handleStatusChange} 
        handleDelete={handleDelete} 
      />
      <TaskColumn 
        title="Fait" 
        tasks={tasksFait} 
        handleStatusChange={handleStatusChange} 
        handleDelete={handleDelete} 
      />
    </div>
  );
}

export default TaskList;
