import React, { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';
import TaskForm from './components/TaskForm';  
import TaskList from './components/TaskList'; 

function App() {
  const [tasks, setTasks] = useState([]);
  const [textTask, setTextTask] = useState('');
  const [hasLoaded, setHasLoaded] = useState(false); 

  const handleChange = (e) => {
    setTextTask(e.target.value);
  };

  const handleStatusChange = (id, newStatus) => {
    setTasks((prevTasks) => 
      prevTasks.map((task) => task.id === id ? { ...task, status: newStatus } : task)
    );
  };

  const handleSubmit = (e, status) => {
    e.preventDefault();
    if (textTask.trim() !== '') {
      setTasks((prevTasks) => [...prevTasks, { id: uuidv4(), text: textTask, status }]);
      setTextTask('');
    }
  };

  const handleDelete = (id) => {
    setTasks((prevTasks) => prevTasks.filter((task) => task.id !== id));
  };

  useEffect(() => {
    const storedTasks = localStorage.getItem('tasks');
    if (storedTasks) {
      setTasks(JSON.parse(storedTasks));
    }
    setHasLoaded(true);
  }, []);

  useEffect(() => {
    if (hasLoaded) {
      localStorage.setItem('tasks', JSON.stringify(tasks));
    }
  }, [tasks, hasLoaded]);

  return (
    <div>
      <h1>Liste de TÃ¢ches</h1>
      <TaskForm textTask={textTask} handleChange={handleChange} handleSubmit={handleSubmit} />
      <TaskList tasks={tasks} handleStatusChange={handleStatusChange} handleDelete={handleDelete} />
    </div>
  );
}

export default App;
