import React from 'react';
import { Draggable } from 'react-beautiful-dnd';
import { FaGripLines } from 'react-icons/fa';

function TaskItem({ task, index, handleStatusChange, handleDelete }) {
  console.log("Rendering TaskItem:", task.id);

  return (
    <Draggable draggableId={task.id.toString()} index={index}>
      {(provided, snapshot) => (  // Fonction de rendu correcte
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}  // Place le handleProps ici pour rendre l'élément draggable
          style={{
            ...provided.draggableProps.style,
            backgroundColor: snapshot.isDragging ? '#f4f4f4' : '#fff',  // Optionnel, indication visuelle du drag
            padding: '10px',
            marginBottom: '10px',
            borderRadius: '4px',
            boxShadow: snapshot.isDragging ? '0 2px 10px rgba(0,0,0,0.2)' : 'none',
          }}
        >
          <span>
            <FaGripLines style={{ cursor: 'grab', marginRight: '10px' }} /> {/* Icône de glisser-déposer */}
          </span>

          {/* Le reste de l'élément */}
          {task.text} - {task.status}
          <button type='delete' onClick={() => handleDelete(index)}>Supprimer</button>
          <select value={task.status} onChange={(e) => handleStatusChange(index, e.target.value)}>
            <option value='todo'>A faire</option>
            <option value='in-progress'>En cours</option>
            <option value='done'>Fait</option>
          </select>
        </div>
      )}
    </Draggable>
  );
}

export default TaskItem;
