import React from 'react';
import TaskItem from './TaskItem';  

function TaskList({ tasks, handleStatusChange, handleDelete }) {
  const tasksTodo = tasks.filter(task => task.status === 'A faire');
  const tasksEnCours = tasks.filter(task => task.status === 'En cours');
  const tasksFait = tasks.filter(task => task.status === 'Fait');

  return (
    <div style={{ display: 'flex', justifyContent: 'space-around' }}>
      {/* Colonne "A faire" */}
      <div>
        <h2>A faire</h2>
        {tasksTodo.map((task) => (
          <TaskItem 
            key={task.id}  // Utilisation de l'ID unique
            task={task}
            handleStatusChange={handleStatusChange}
            handleDelete={handleDelete}
          />
        ))}
      </div>

      {/* Colonne "En cours" */}
      <div>
        <h2>En cours</h2>
        {tasksEnCours.map((task) => (
          <TaskItem 
            key={task.id}  // Utilisation de l'ID unique
            task={task}
            handleStatusChange={handleStatusChange}
            handleDelete={handleDelete}
          />
        ))}
      </div>

      {/* Colonne "Fait" */}
      <div>
        <h2>Fait</h2>
        {tasksFait.map((task) => (
          <TaskItem 
            key={task.id}  // Utilisation de l'ID unique
            task={task}
            handleStatusChange={handleStatusChange}
            handleDelete={handleDelete}
          />
        ))}
      </div>
    </div>
  );
}

export default TaskList;
