import React from 'react';
import TaskItem from './TaskItem';  
import { Droppable } from 'react-beautiful-dnd';

function TaskList({ Tasks, handleDelete }) {
  return (
    <div style={{ display: 'flex', justifyContent: 'space-around' }}>
      {/* Colonne "A faire" */}
      <Droppable droppableId="todo">
        {(provided) => (
          <div ref={provided.innerRef} {...provided.droppableProps}>
            <h2>A faire</h2>
            {(Tasks.todo || []).map((task, index) => {
              console.log("Rendering task in 'A faire' column:", task.id);  // Vérification de l'ID
              return (
                <TaskItem 
                  key={task.id}
                  task={task} 
                  index={index} 
                  handleDelete={handleDelete}
                />
              );
            })}
            {provided.placeholder}
          </div>
        )}
      </Droppable>

      {/* Colonne "En cours" */}
      <Droppable droppableId="in-progress">
        {(provided) => (
          <div ref={provided.innerRef} {...provided.droppableProps}>
            <h2>En cours</h2>
            {(Tasks['in-progress'] || []).map((task, index) => {
              console.log("Rendering task in 'En cours' column:", task.id);
              return (
                <TaskItem 
                  key={task.id}
                  task={task} 
                  index={index} 
                  handleDelete={handleDelete}
                />
              );
            })}
            {provided.placeholder}
          </div>
        )}
      </Droppable>

      {/* Colonne "Fait" */}
      <Droppable droppableId="done">
        {(provided) => (
          <div ref={provided.innerRef} {...provided.droppableProps}>
            <h2>Fait</h2>
            {(Tasks.done || []).map((task, index) => {
              console.log("Rendering task in 'Fait' column:", task.id);
              return (
                <TaskItem 
                  key={task.id}
                  task={task} 
                  index={index} 
                  handleDelete={handleDelete}
                />
              );
            })}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </div>
  );
}

export default TaskList;
