import React, { useState, useEffect } from 'react';
import TaskForm from './components/TaskForm';  
import TaskList from './components/TaskList'; 
import { DragDropContext } from 'react-beautiful-dnd';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [Tasks, setTasks] = useState({
    todo: [],
    'in-progress': [],
    done: []
  });
  const [TextTasks, setTextTask] = useState('');
  const [hasLoaded, setHasLoaded] = useState(false); 

  const handleChange = (e) => {
    setTextTask(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (TextTasks.trim() !== '') {
      const newTask = { id: uuidv4(), text: TextTasks, status: 'todo' };
      console.log('New Task ID:', newTask.id);  // Vérification de l'ID de la nouvelle tâche
      setTasks((prevTasks) => ({
        ...prevTasks,
        todo: [...prevTasks.todo, newTask]
      }));
      setTextTask('');
    }
  };

  const handleDelete = (taskId, status) => {
    setTasks((prevTasks) => ({
      ...prevTasks,
      [status]: prevTasks[status].filter(task => task.id !== taskId)
    }));
  };

  const handleOnDragEnd = (result) => {
    const { source, destination } = result;
    console.log("Source:", source, "Destination:", destination);

    if (!destination) return;

    if (source.droppableId === destination.droppableId && source.index === destination.index) {
      return;
    }

    const sourceList = Array.from(Tasks[source.droppableId]);
    const [movedTask] = sourceList.splice(source.index, 1);

    const destinationList = Array.from(Tasks[destination.droppableId]);

    movedTask.status = destination.droppableId;
    destinationList.splice(destination.index, 0, movedTask);

    setTasks((prevTasks) => ({
      ...prevTasks,
      [source.droppableId]: sourceList,
      [destination.droppableId]: destinationList
    }));
  };

  useEffect(() => {
    const storedTasks = localStorage.getItem('Tasks');
    if (storedTasks) {
      try {
        const parsedTasks = JSON.parse(storedTasks);
        console.log('Tâches chargées depuis le localStorage :', parsedTasks);
        setTasks({
          todo: parsedTasks.todo || [],
          'in-progress': parsedTasks['in-progress'] || [],
          done: parsedTasks.done || []
        });
      } catch (error) {
        console.error('Erreur lors de la lecture des tâches depuis le localStorage:', error);
        // Initialiser avec des tableaux vides si les données sont invalides
        setTasks({
          todo: [],
          'in-progress': [],
          done: []
        });
      }
    } else {
      // Initialiser avec des tableaux vides si aucune donnée n'est trouvée
      setTasks({
        todo: [],
        'in-progress': [],
        done: []
      });
    }
    setHasLoaded(true);
  }, []);

  useEffect(() => {
    if (hasLoaded) {
      localStorage.setItem('Tasks', JSON.stringify(Tasks));
    }
  }, [Tasks, hasLoaded]);

  return (
    <div>
      <h1>Liste de Tâches</h1>
      <TaskForm TextTasks={TextTasks} handleChange={handleChange} handleSubmit={handleSubmit} />
      <DragDropContext onDragEnd={handleOnDragEnd}>
        <TaskList Tasks={Tasks} handleDelete={handleDelete} />
      </DragDropContext>
    </div>
  );
}

export default App;
