import React, {useState} from 'react';

function App(){
  const [Tasks, setTasks] = useState(['hasard']);
  const [TextTasks, setTextTask] = useState('');

  setTasks([...Tasks, TextTasks]);

  return (
    <div>
      <h1>Liste de Tâches</h1>
      <label>
          Entrez vôtre tâche: <input name="tache" />
      </label>
      <button>
        Enregistrer
      </button>
      {Tasks.map((task) => (
        <p id={task.id}>{task}</p>
      )
      )};
    </div>
  )
}

export default App;
