import React, {useState} from 'react';

function App(){
  const [Tasks, setTasks] = useState([]);
  const [TextTasks, setTextTask] = useState('');

  const handleChange = (e) => {
    setTextTask(e.target.value);
  }

  const handleSubmit = () => {

    if (TextTasks.trim() !== ''){
      setTasks([...Tasks, TextTasks]);
      setTextTask('');
    }
    //eventpreventDefault();
  } 

  return (
    <div>
      <h1>Liste de Tâches</h1>
      <form onSubmit={handleSubmit}>
        <label>
            Entrez vôtre tâche: <input name="tache" value={TextTasks} onChange={handleChange} />
        </label>
        <button type='submit'>
          Enregistrer
        </button>
      </form>
      {Tasks.map((task) => (
        <p>{task}</p>
      )
      )};
    </div>
  )
}

export default App;
