import React from 'react';
import TaskItem from './TaskItem';  
import { Droppable } from 'react-beautiful-dnd';

function TaskList({ Tasks, handleStatusChange, handleDelete }) {
  const tasksTodo = Tasks.filter(task => task.status === 'todo');
  const tasksInProgress = Tasks.filter(task => task.status === 'in-progress');
  const tasksDone = Tasks.filter(task => task.status === 'done');

  return (
    <div style={{ display: 'flex', justifyContent: 'space-around' }}>
      {/* Colonne "A faire" */}
      <Droppable droppableId="todo">
        {(provided) => (
          <div ref={provided.innerRef} {...provided.droppableProps}>
            <h2>A faire</h2>
            {tasksTodo.map((task, index) => {
              console.log("Rendering task in 'A faire' column:", task.id);  // Ajout du console.log pour vérifier l'ID
              return (
                <TaskItem 
                  key={task.id}
                  task={task} 
                  index={index} 
                  handleStatusChange={handleStatusChange} 
                  handleDelete={handleDelete}
                />
              );
            })}
            {provided.placeholder}
          </div>
        )}
      </Droppable>

      {/* Colonne "En cours" */}
      <Droppable droppableId="in-progress">
        {(provided) => (
          <div ref={provided.innerRef} {...provided.droppableProps}>
            <h2>En cours</h2>
            {tasksInProgress.map((task, index) => {
              console.log("Rendering task in 'En cours' column:", task.id);
              return (
                <TaskItem 
                  key={task.id}
                  task={task} 
                  index={index} 
                  handleStatusChange={handleStatusChange} 
                  handleDelete={handleDelete}
                />
              );
            })}
            {provided.placeholder}
          </div>
        )}
      </Droppable>

      {/* Colonne "Fait" */}
      <Droppable droppableId="done">
        {(provided) => (
          <div ref={provided.innerRef} {...provided.droppableProps}>
            <h2>Fait</h2>
            {tasksDone.map((task, index) => {
              console.log("Rendering task in 'Fait' column:", task.id);
              return (
                <TaskItem 
                  key={task.id}
                  task={task} 
                  index={index} 
                  handleStatusChange={handleStatusChange} 
                  handleDelete={handleDelete}
                />
              );
            })}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </div>
  );
}

export default TaskList;
